import { ADD_FAVORITE, DELETE_FAVORITE, FETCH_DATA, SET_STATUS, RESET_STATUS, LOADING, LOADED } from './types';

const initialState = {
    favorites: [],
    loadingFavorites: false,
    foundDog: null
}

export const favoriteReducer = (state = initialState, action) => {

    switch (action.type) {
        case ADD_FAVORITE:
            return {...state, favorites:[...state.favorites,action.payload]};
        case DELETE_FAVORITE:
            return {...state,favorites:state.filter(dog => dog.id !== action.payload)};
        case FETCH_DATA:
            return {...state, favorites: action.payload}
        default:
            return state;
    }
}

export const statusReducer = (state = { loadingId: '', loadingFavorites: false }, action) => {
    switch (action.type) {
        case SET_STATUS:
            return { ...state, loadingId: action.payload };
        case RESET_STATUS:
            return { ...state, loadingId: '', loadingFavorites: false };
        case LOADING:
            return { ...state, loadingFavorites: true }
        case LOADED:
            return { ...state, loadingFavorites: false }
        default:
            return state;
    }
}
