import React, { Component } from "react";
import {
    Button,
    Form,
    FormGroup,
    Label,
    Input,
    FormFeedback,
} from "reactstrap";
import { Container, Row, Col } from "reactstrap";
import { Formik } from "formik";
import * as Yup from "yup";

const validationSchema = Yup.object().shape({
    name: Yup.string().required("Zorunlu alan"),
    surname: Yup.string().required("Zorunlu alan"),
    company: Yup.string().required("Zorunlu alan"),
    email: Yup.string().required("Zorunlu alan"),
    phone: Yup.string().required("Zorunlu alan"),
    website: Yup.string().required("Zorunlu alan"),
});

export class Reactstrap extends Component {
    render() {
        return (
            <div>
                <Container>
                    <Formik
                        initialValues={{
                            name: "",
                            surname: "",
                            company: "",
                            email: "",
                            phone: "",
                            website: "",
                            radio: "",
                            checkbox: "",
                        }}
                        validationSchema={validationSchema}
                        onSubmit={(values) => {
                            //alert("Form Gönderildi.")
                            console.log(values);
                        }}
                    >
                        {({
                            values,
                            errors,
                            touched,
                            handleChange,
                            handleBlur,
                            handleSubmit,
                            isSubmitting,
                        }) => (
                                <Form onSubmit={handleSubmit}>
                                    <Row>
                                        <Col>
                                            <h2>Contact Us</h2>
                                        </Col>
                                    </Row>
                                    <Row>
                                        <Col>
                                            <FormGroup>
                                                <Label for="Name">
                                                    <b>First Name</b>
                                                </Label>
                                                <Input
                                                    type="text"
                                                    name="name"
                                                    id="name"
                                                    placeholder="Enter Name"
                                                    onChange={handleChange}
                                                    value={values.name}
                                                    invalid={errors.name}
                                                />
                                                {errors.name && (
                                                    <FormFeedback>{errors.name}</FormFeedback>
                                                )}
                                            </FormGroup>
                                            <br />
                                            <FormGroup>
                                                <Label for="Company">
                                                    <b>Company</b>
                                                </Label>
                                                <Input
                                                    type="text"
                                                    name="company"
                                                    id="company"
                                                    placeholder="Enter Company"
                                                    onChange={handleChange}
                                                    value={values.company}
                                                    invalid={errors.company}

                                                />
                                                {errors.company && (
                                                    <FormFeedback>{errors.company}</FormFeedback>
                                                )}
                                                <br />
                                            </FormGroup>
                                            <FormGroup>

                                                <Label for="Email">
                                                    <b>Email address</b>
                                                </Label>
                                                <Input
                                                    type="email"
                                                    name="email"
                                                    id="email"
                                                    placeholder="email"
                                                    onChange={handleChange}
                                                    value={values.email}
                                                    invalid={errors.email}
                                                />
                                                <br />
                                            </FormGroup>
                                            {errors.email && <FormFeedback>{errors.email}</FormFeedback>}
                                        </Col>
                                        <Col>

                                            <FormGroup><Label for="LastName">
                                                <b>Last Name</b>
                                            </Label>
                                                <Input
                                                    type="text"
                                                    name="surname"
                                                    id="surname"
                                                    placeholder="Enter Surname"
                                                    onChange={handleChange}
                                                    value={values.surname}
                                                    invalid={errors.surname}
                                                />
                                                <br />
                                            </FormGroup>
                                            {errors.surname && <FormFeedback>{errors.surname}</FormFeedback>}
                                            <FormGroup><Label for="Phone">
                                                <b>Phone Number</b>
                                            </Label>
                                                <Input
                                                    type="text"
                                                    name="phone"
                                                    id="phone"
                                                    placeholder="Phone Number"
                                                    onChange={handleChange}
                                                    value={values.phone}
                                                    invalid={errors.phone}
                                                />
                                                <br />
                                            </FormGroup>
                                            {errors.phone && <FormFeedback>{errors.phone}</FormFeedback>}
                                            <FormGroup><Label for="Website ">
                                                <b>Your Website</b>
                                            </Label>
                                                <Input
                                                    type="text"
                                                    name="website"
                                                    id="website"
                                                    placeholder="Please include http://url"
                                                    onChange={handleChange}
                                                    value={values.website}
                                                />
                                                {errors.phone && (
                                                    <FormFeedback>{errors.phone}</FormFeedback>
                                                )}
                                            </FormGroup>



                                        </Col>
                                    </Row>

                                    <FormGroup tag="fieldset">
                                        <legend>When is the best time of day to reach you?</legend>
                                        <FormGroup check>
                                            <Label check>
                                                <Input
                                                    onChange={handleChange}
                                                    value={values.radio}
                                                    type="radio"
                        /*checked="true"*/ name="radio"
                                                />{" "}
                      Morning
                    </Label>
                                        </FormGroup>
                                        <FormGroup check>
                                            <Label check>
                                                <Input
                                                    onChange={handleChange}
                                                    value={values.radio}
                                                    type="radio"
                                                    name="radio"
                                                />{" "}
                      Evening
                    </Label>
                                        </FormGroup>
                                    </FormGroup>
                                    <FormGroup check>
                                        <Label check>
                                            <h6>Would you like to recieve our email newsletter?</h6>
                                            <Input
                                                onChange={handleChange}
                                                value={values.checkbox}
                                                type="checkbox"
                                            />{" "}
                    Sure!
                  </Label>
                                    </FormGroup>
                                    <Button color="primary">Submit</Button>
                                </Form>
                            )}
                    </Formik>
                </Container>
            </div>
        );
    }
}

export default Reactstrap;
