import React, { Component } from 'react';
import { Button } from "reactstrap";
import { connect } from "react-redux";
import {addFavorite,deleteFavorite,showStatus} from "../redux/actions";

const FavoriteActions = (props) => {
    let isDisabled = props.loadingId === props.id ? true : false;

    const foundDog = props.favorites.find((favorites)=> favorites.dogId ===props.id)
    return (
        <div>
            {
                foundDog ?
                    <Button disabled={isDisabled} color="warning" 
                    onClick={() => { props.deleteFavorite(foundDog.id, props.id) }}>Favorite âœ“</Button>
                    : <Button disabled={isDisabled} color="primary" 
                    onClick={() => { props.addFavorite(props.id) }}>Add to Favorites</Button>
            }
        </div>
    );
};

const mapStateToProps = state => {
    return {
        favorites: state.favoriteReducer,
        loadingId: state.statusReducer.loadingId
    }
}

const mapDispatchToProps = {
    addFavorite: addFavorite,
    deleteFavorite: deleteFavorite,
    showStatus: showStatus
}

export default connect(mapStateToProps, mapDispatchToProps)(FavoriteActions);
